-- ----------------------------
-- Table structure for `leacot`
-- ----------------------------
DROP TABLE IF EXISTS `leacot`;
CREATE TABLE `leacot` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '留言ID',
  `content` varchar(2000) COLLATE utf8_bin DEFAULT NULL COMMENT '留言内容',
  `leacots_time` datetime DEFAULT NULL COMMENT '留言时间',
  `leacots_user` varchar(50) COLLATE utf8_bin DEFAULT NULL COMMENT '留言用户',
  `reply_id` int(11) DEFAULT NULL COMMENT '关联回复内容',
  `status` int(2) DEFAULT NULL COMMENT '回复状态',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=27 DEFAULT CHARSET=utf8 COLLATE=utf8_bin ROW_FORMAT=COMPACT;

