-- ----------------------------
-- Table structure for `pgtest`
-- ----------------------------
DROP TABLE IF EXISTS `pgtest`;
CREATE TABLE `pgtest` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '评测ID',
  `check_scores` int(11) DEFAULT NULL COMMENT '多选',
  `radio_scores` int(5) DEFAULT NULL COMMENT '单选分数',
  `pgtest_time` datetime DEFAULT NULL COMMENT '评测时间',
  `user_op` varchar(255) COLLATE utf8_bin DEFAULT NULL COMMENT '评测用户',
  `taotiid` int(11) DEFAULT NULL,
  `total` int(11) DEFAULT NULL COMMENT '总分',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=16 DEFAULT CHARSET=utf8 COLLATE=utf8_bin ROW_FORMAT=COMPACT;

