-- ----------------------------
-- Table structure for `topic`
-- ----------------------------
DROP TABLE IF EXISTS `topic`;
CREATE TABLE `topic` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '题目ID',
  `topic_content` varchar(1000) COLLATE utf8_bin DEFAULT NULL COMMENT '题目内容',
  `refer_answer` varchar(2000) COLLATE utf8_bin DEFAULT NULL COMMENT '题目参考答案',
  `topic_time` datetime DEFAULT NULL COMMENT '题目创建时间',
  `user_op` varchar(50) COLLATE utf8_bin DEFAULT NULL COMMENT '题目操作用户',
  `taotiid` int(11) DEFAULT NULL COMMENT '什么类型的题目',
  `optiona` varchar(255) COLLATE utf8_bin DEFAULT NULL COMMENT 'A选项',
  `optionb` varchar(255) COLLATE utf8_bin DEFAULT NULL COMMENT 'b',
  `optionc` varchar(255) COLLATE utf8_bin DEFAULT NULL,
  `optiond` varchar(255) COLLATE utf8_bin DEFAULT NULL COMMENT 'd',
  `type` varchar(255) COLLATE utf8_bin DEFAULT NULL COMMENT '单选还是多选',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=12 DEFAULT CHARSET=utf8 COLLATE=utf8_bin ROW_FORMAT=COMPACT;

